(4, '49a1f5b1-f220-4ee7-8070-c33768015c35', 'Admin Pending Job Reminder Notification', '<p><font face=\"Helvetica\">Dear {cse_name},</font></p><p><font face=\"Helvetica\">Here is a reminder that {job_ref} is pending. Please view the job details below and complete the job on schedule.</font></p><p><font face=\"Helvetica\">JOB DETAILS</font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • TECHNICIAN ASSIGNED: {technician_name}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • TECHNICIAN TELEPHONE: {technician_phone_no}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • QA TECHNICIAN ASSIGNED: {qa_technician_phone_no}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • JOB SERVICE CATEGORY: {category}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SERVICE CATEGORY: {service}&nbsp;&nbsp;</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SUB-SERVICE CATEGORY: {sub_service}&nbsp;</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • JOB STATUS: {job_status}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SCHEDULED DATE/TIME: {date_time}</b></font></p><p><span style=\"font-family: Helvetica;\">Please click {url} to view images, videos and additional details of {job_ref}.</span><br></p>', 'Dear {cse_name},\n\nHere is a reminder that {job_ref} is pending. Please view the job details below and complete the job on schedule.', 'ADMIN_PENDING_JOB_REMINDER_NOTIFICATION', '2021-05-27 20:05:29', '2021-06-26 06:00:35', NULL),
(5, '87c4e531-22f9-4b97-b424-c65f4da7ba63', 'Admin Message', '<p><font face=\"Helvetica\">Dear {admin_title},</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">You have a NEW message from the FixMaster {admin_title}.&nbsp;</font></p><p><font face=\"Helvetica\">Please log into your portal to view your message!</font></p><p><font face=\"Helvetica\">Please click here to login to the Admin portal.&nbsp;</font></p>', 'Dear {admin_title},\n\nYou have a NEW message from the FixMaster {admin_title}.\nPlease log into your portal to view your message.\nPlease click here to login to the Admin portal. \n\nThank you', 'ADMIN_SENT_ADMIN_MESSAGE_NOTIFICATION', '2021-05-27 20:08:54', '2021-06-26 06:00:59', NULL),
(6, 'd45acd36-959f-4de2-ab0c-dfac00bce8c7', 'Admin Message', '<p>Dear {cse_name},</p><p><br></p><p>You have a NEW message from the FixMaster {admin_title}.&nbsp;</p><p>Please log into your portal to view your message!</p><p><br></p><p>Please click <a href=\"{url}\">here</a> to login to the CSE portal.&nbsp;</p><p><br></p><p>Thank you</p><p>FixMaster Team</p>', 'Dear {cse_name},\n\n\n\nYou have a NEW message from the FixMaster {admin_title}. \n\nPlease log into your portal to view your message!', 'ADMIN_SENT_CSE_MESSAGE_NOTIFICATION', '2021-05-27 20:11:01', '2021-05-27 20:11:01', NULL),
(7, 'c88c536b-5734-449b-b5f8-bb7e7bd2ec20', 'Admin Message', '<p>Dear {first_name} {last_name}</p><p><br></p><p>You have a NEW message from the FixMaster Administrator.&nbsp;</p><p>Please log into your portal to view your message!</p><p><br></p><p>Please click <a href=\"{url}\">here</a> to login and view message details.&nbsp;</p><p><br></p><p>Thank you</p><p>FixMaster Team</p>', 'Dear {first_name} {last_name}\n\n\n\nYou have a NEW message from the FixMaster Administrator. \n\nPlease log into your portal to view your message!', 'ADMIN_SENT_CUSTOMER_MESSAGE_NOTIFICATION', '2021-05-27 20:12:28', '2021-05-27 20:12:28', NULL),
(8, 'af791809-1673-4e3c-86b5-c882741c7010', 'Admin Message', '<p>Dear {supplier_name},</p><p><br></p><p>You have a NEW message from the FixMaster Administrator.&nbsp;</p><p>Please log into your portal to view your message!</p><p>Please click {url} to login to the Supplier portal.&nbsp;</p>', 'Dear {supplier_name},\n\n\n\nYou have a NEW message from the FixMaster Administrator. \n\nPlease log into your portal to view your message!', 'ADMIN_SENT_SUPPLIER_MESSAGE_NOTIFICATION', '2021-05-27 20:13:30', '2021-06-26 06:01:22', NULL),
(9, '6e4ff0f1-096f-48f6-9799-a8fab7d56b07', 'Admin Message', '<p>Dear {technician_name},</p><p><br></p><p>You have a NEW message from the FixMaster {admin_title}.&nbsp;</p><p>Please log into your portal to view your message!</p><p><br></p><p>Please click <a href=\"{url}\">here</a> to login to the Technician portal.&nbsp;</p><p><br></p><p>Thank you</p><p>FixMaster Team</p>', 'Dear {technician_name},\n\n\n\nYou have a NEW message from the FixMaster {admin_title}. \n\nPlease log into your portal to view your message!', 'ADMIN_SENT_TECHNICIAN_MESSAGE_NOTIFICATION', '2021-05-27 20:14:42', '2021-05-27 20:14:42', NULL),
(10, 'e5ae6e0c-d3b9-4425-b22e-8c257ddbdeae', 'Admin Message', '<p>Dear {qa_name},</p><p><br></p><p>You have a NEW message from the FixMaster {admin_title}.&nbsp;</p><p>Please log into your portal to view your message!</p><p><br></p><p>Please click {url}&nbsp;to login to your dashboard and view details of the message.&nbsp;</p><p><br></p><p>Thank you</p><p>FixMaster Team</p>', 'Dear {qa_name},\n\n\n\nYou have a NEW message from the FixMaster {admin_title}. \n\nPlease log into your portal to view your message!', 'ADMIN_SENT_QA_MESSAGE_NOTIFICATION', '2021-05-27 20:15:42', '2021-06-26 06:00:08', NULL),
(11, '0e091628-6c4e-4868-a1fe-bb7be6c499a2', 'Admin Warranty Claim Notification', '<p><span style=\"color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;; font-size: 13px;\">Hello {firstname} {lastname},</span><span class=\"Apple-converted-space\" style=\"color: rgb(0, 0, 0); font-family: &quot;Helvetica Neue&quot;; font-size: 13px;\">&nbsp;</span><br></p><p class=\"p2\" style=\"margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 13px; line-height: normal; font-family: &quot;Helvetica Neue&quot;; min-height: 15px; color: rgb(0, 0, 0);\"><br></p><p class=\"p1\" style=\"margin-bottom: 0px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 13px; line-height: normal; font-family: &quot;Helvetica Neue&quot;; color: rgb(0, 0, 0);\">A Warranty Claim for&nbsp;<span style=\"font-weight: bolder;\">{job_ref}</span>, has been issued by&nbsp;<span style=\"font-weight: bolder;\">{client_name}</span>. Thank you.</p>', 'Hello {firstname} {lastname}, \nA Warranty Claim for {job_ref}, has been issued by {client_name}. Thank you.', 'ADMIN_WARRANTY_CLAIM_NOTIFICATION', '2021-05-27 20:31:31', '2021-06-24 22:49:31', NULL),
(12, 'af3813e8-00a1-44de-9d75-b54d09ee20b7', 'Warranty Claim Resolved', '<p>Dear {firstname} {lastname},</p><p><br></p><p>Your warranty claim has been successfully resolved by FixMaster.&nbsp;</p><p>FixMaster value proposition to her esteemed customers is to deliver quality services ALWAYS.&nbsp;</p><p>Thank you for allowing us serve you!</p><p>Your feedback is very important to us- please rate the CSE and the overall FixMaster service.</p><p><br></p><p>Thank you.</p><p>Fix Master Team</p>', 'Dear {firstname} {lastname},\n\nYour warranty claim has been successfully resolved by FixMaster. \nFixMaster value proposition to her esteemed customers is to deliver quality services ALWAYS. \nThank you for allowing us serve you.\nYour feedback is very important to us- please rate the CSE and the overall FixMaster service.\n\nThank you.\nFix Master Team', 'ADMIN_WARRANTY_CLAIM_RESOLVED_NOTIFICATION', '2021-05-27 20:34:50', '2021-05-27 20:34:50', NULL),
(13, 'd4a10634-c9e9-41c1-a35c-9c43b88f45ca', 'Application Rejected', '<p>Dear&nbsp;{firstname}&nbsp;{lastname},</p><p>Unfortunately, your application to collaborate with FixMaster as a supplier was not successful.&nbsp;</p><p>We would not be proceeding with the on-boarding exercise.</p><p>FixMaster wishes you the best in your future endeavors.</p><p>Thank you,</p><p>FixMaster Team</p>', 'Dear {firstname} {lastname},\n\nUnfortunately, your application to collaborate with FixMaster as a supplier was not successful. \n\nWe would not be proceeding with the on-boarding exercise.\n\nFixMaster wishes you the best in your future endeavors.\n\nThank you,\n\nFixMaster Team', 'SUPPLIER_APPLICATION_REJECTED', '2021-06-14 15:51:37', '2021-06-14 15:52:05', NULL),
(14, 'dead50af-6c87-4d7e-bd66-f26f851b0473', 'Message Notification', '<p>Dear {firstname} {lastname},</p><p>You have a NEW message from {supplier_ name}.</p><p>Please log into your portal to view your message!</p><p><br></p><p>Please click <a href=\"{url}\">here</a> to login and view message details.&nbsp;</p><p><br></p><p>Thank you</p><p>FixMaster Team</p>', 'Dear {firstname} {lastname},\n\nYou have a NEW message from {supplier_ name}.\n\nPlease log into your portal to view your message!\n\n\n\nPlease click here to login and view message details. \n\n\n\nThank you\n\nFixMaster Team', 'SUPPLIER_SENT_CSE_MESSAGE_NOTIFICATION', '2021-06-14 15:54:33', '2021-06-14 15:54:33', NULL),
(15, '574801cb-536e-4d7d-9ae0-f9d4cfed79dd', 'Account Creation', '<p>Dear {firstname} {lastname},&nbsp;</p><p>Congratulations, and account has been created for you on the fixed master platform. Please find your login details below&nbsp;</p><p>Name:&nbsp;{firstname}&nbsp;{lastname}</p><p>Username: {username}</p><p>Password:&nbsp;</p><p>Please update your password and your account details as soon as you log in.</p><p>We are happy to have you on board.</p>', 'Dear {firstname} {lastname}, \n\nCongratulations, and account has been created for you on the fixed master platform. Please find your login details below \n\nName: {firstname} {lastname}\n\nUsername: {username}\n\nPassword: {password}\n\nPlease update your password and your account details as soon as you log in.\n\nWe are happy to have you on board.', 'SUPPLIER_ACCOUNT_CREATION_NOTIFICATION', '2021-06-14 19:30:16', '2021-06-14 19:30:16', NULL),
(16, '0c42afd0-e3ed-4d3f-97ac-d8352450e09d', 'Accepted Invoice', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Your invoice for RFQ: <b>{job_ref}</b> has been approved. Please be on standby as you will be contacted anytime soon to make delivery.</p>', 'Dear {firstname} {lastname},\n\nYour invoice for job id: {job_ref} has been approved. Please be on standby as you will be contacted anytime soon to make delivery.', 'SUPPLIER_ACCEPTED_INVOICE_NOTIFICATION', '2021-06-14 19:31:41', '2021-07-05 07:43:10', NULL),
(17, '0dfa94d2-921b-466d-80a6-97334abc2dd9', 'Application Submitted', '<p>Dear&nbsp;{firstname} ,</p><p>Your application to be one of our registered suppliers has been received. You will be contacted for an interview shortly if you meet our criteria.</p><p>Thank you.</p>', 'Dear {firstname} {lastname},\n\nYour application to be one of our registered suppliers has been received. You will be contacted for an interview shortly if you meet our criteria.\n\nThank you.', 'SUPPLIER_APPLICATION_SUBMITTED', '2021-06-14 19:32:36', '2021-06-14 19:32:36', NULL),
(18, '06942a8a-432f-4c7b-9425-ba1b0e424c9d', 'Application Successful', '<p>Dear&nbsp;{firstname} ,</p><p>Your application to be one of our registered suppliers has been reviewed and you have met our criteria.</p><p>An account will be created for you shortly and you will be notified as soon as it is created.</p><p>Thank you.</p>', 'Dear {firstname} {lastname},\n\nYour application to be one of our registered suppliers has been reviewed and you have met our criteria.\n\nAn account will be created for you shortly and you will be notified as soon as it is created.\n\nThank you.', 'SUPPLIER_APPLICATION_SUCCESSFUL', '2021-06-14 19:33:21', '2021-06-14 19:33:21', NULL),
(19, '89da0b77-4d06-4310-842a-cc5c0a8a3de9', 'Declined Supplier Invoice', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Your invoice sent for RFQ:&nbsp;<b>{job_ref}</b> was not selected at this time. Please try again next time.</p><p>Please note that\'s your delivery fee and your delivery time will determine if you are selected for delivery or not.</p>', 'Dear {firstname} {lastname},\n\nYour invoice sent for RFQ: {job_ref} was not selected at this time. Please try again next time.\n\nPlease note that\'s your delivery fee and your delivery time will determine if you are selected for delivery or not.', 'SUPPLIER_DECLINED_INVOICE_NOTIFICATION', '2021-06-14 19:34:26', '2021-06-17 20:46:13', NULL),
(20, '268679e2-2698-4d57-9256-f2d447ad1cbb', 'Dispatch Accepted Invoice', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Thank you, materials dispatched for job id:&nbsp;{job_ref} has been received and is in perfect condition.</p><p>Payment may take 7 working days to process with percentage health as retention till after the warranty period elapses.</p>', 'Dear {firstname} {lastname},\n\nThank you, materials dispatched for job id: {job_ref} has been received and is in perfect condition.\n\nPayment may take 7 working days to process with percentage health as retention till after the warranty period elapses.', 'SUPPLIER_DISPATCHED_ACCEPTED_INVOICE_NOTIFICATION', '2021-06-14 19:35:58', '2021-06-17 21:17:02', NULL),
(21, 'ef0e3813-2e3b-42a3-86a1-75cd718b68d7', 'Dispatch Invoice', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Your materials <b>({dispatch_id})</b> for RFQ ID: <b>{rfq_ref}</b>&nbsp; has been dispatched.</p><p>Please be on standby if you will need to be contacted after the CSE has received the materials.</p><p>Thank you</p>', 'Dear {firstname} {lastname},\n\nYour materials for job id: {rfq_ref} has been dispatched.\n\nPlease be on standby if you will need to be contacted after the CSE has received the materials.\n\nThank you', 'SUPPLIER_DISPATCHED_INVOICE_NOTIFICATION', '2021-06-14 19:36:55', '2021-07-05 06:42:08', NULL),
(22, 'ddf01617-b7f7-4b26-abb0-53ed12c10fce', 'Dispatch Paid Invoice', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Your invoice for job id:&nbsp; has been paid for. Please make delivery as quickly as possible, on or before your estimated delivery date.</p><p>Thank you.</p>', 'Dear {firstname} {lastname},\n\nYour invoice for job id: {job_ref} has been paid for. Please make delivery as quickly as possible, on or before your estimated delivery date.\n\nThank you.', 'SUPPLIER_DISPATCHED_PAID_INVOICE_NOTIFICATION', '2021-06-14 19:37:47', '2021-06-14 19:37:47', NULL),
(23, 'e14c69a1-05bf-4262-b7d5-1ed7dec984ae', 'Dispatch Rejected Invoice', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Your invoice for job id:&nbsp; has been declined. This could be due to the fact that you did not meet the criteria for selecting the best invoice.</p><p>Please give better quotes next time in terms of cost and delivery date to be able to stand the chance of being accepted.</p><p>Thank you</p>', 'Dear {firstname} {lastname},\n\nYour invoice for job id: {job_ref} has been declined. This could be due to the fact that you did not meet the criteria for selecting the best invoice.\n\nPlease give better quotes next time in terms of cost and delivery date to be able to stand the chance of being accepted.\n\nThank you', 'SUPPLIER_DISPATCHED_REJECTED_INVOICE_NOTIFICATION', '2021-06-14 19:38:35', '2021-06-14 19:38:35', NULL),
(24, '21830250-2219-459c-871a-7beb21c55a4a', 'New Request For Quote (RFQ)', '<p><span style=\"background-color: transparent;\">Dear {customer_name}</span></p><p><span style=\"background-color: transparent;\"><br></span></p><p><span style=\"background-color: transparent;\">A new <b>REQUEST FOR QUOTATION</b> {job_ref} is available for execution. Kindly\nlogin to your account to QUOTE for the job NOW!</span><span style=\"background-color: transparent;\"><br></span></p>\n<p style=\"margin-right: 1.44cm; margin-bottom: 0.28cm; line-height: 108%; orphans: 0; widows: 0\">\n<font color=\"#222222\"><font face=\"Arial, serif\"><font style=\"font-size: 10pt\" size=\"2\">Please\nclick {url} </font></font></font><font color=\"#222222\"><font face=\"Arial, serif\"><font style=\"font-size: 10pt\" size=\"2\">to\nview details and complete the quote. </font></font></font>\n</p>\n<p style=\"margin-right: 1.44cm; margin-bottom: 0.28cm; line-height: 108%; orphans: 0; widows: 0\">\n<font color=\"#222222\"><font face=\"Arial, serif\"><font style=\"font-size: 10pt\" size=\"2\">Your\nrating counts! Kindly respond promptly with best prices and deliver\nONLY quality items on schedule!</font></font></font></p>\n\n<p><style type=\"text/css\">p { margin-bottom: 0.25cm; direction: ltr; line-height: 115%; text-align: left; orphans: 2; widows: 2; background: transparent }a:link { color: #0563c1; text-decoration: underline }</style></p>', 'Dear {customer_name}\n\n\n\nA new REQUEST FOR QUOTATION {job_ref} is available for execution. Kindly login to your account to QUOTE for the job NOW!\n\nPlease click {url} to view details and complete the quote.\n\nYour rating counts! Kindly respond promptly with best prices and deliver ONLY quality items on schedule!\n\nThank you for collaborating with FixMaster!', 'SUPPLIER_NEW_RFQ_NOTIFICATION', '2021-06-15 18:17:24', '2021-06-17 21:41:39', NULL),
(25, 'beab598a-6b87-462b-9ad1-3822aa7a2e8d', 'Supplier Sent Invoice Notification', '<p>Dear&nbsp;{firstname} {lastname},</p><p>Your Invoice has been received for <b>{rfq_ref}</b> RFQ, and you\'ll be contacted for delivery if you meet the selection requirements.</p><p>Please be on standby.</p>', 'Dear {firstname} {lastname},\n\nYour Invoice has been received, and you\'ll be contacted for delivery if you meet the selection requirements.\n\nPlease be on standby.\n\nThank you.', 'SUPPLIER_SENT_INVOICE_NOTIFICATION', '2021-06-14 19:40:42', '2021-07-05 06:10:10', NULL),
(26, '5f433627-6c70-488d-8e12-f1af4fe4b0e6', 'Supplier Warranty Claim Notification', '<p>Dear&nbsp;{first_name} {last_name},</p><p>A warranty claim has been triggered for Job id: <b>{job_ref}</b>. Our CSE will contact you for a parts replacement if the reason for this claim is the material you supplied.<br></p><p>Where you are not available and replacement is needed, A new supplier will be selected and paid from your balance.</p><p>Thank you.</p>', 'Dear {first_name} {last_name},\n\nA warranty claim has been triggered for Job id: {job_ref}. Our CSE will contact you for a parts replacement if the reason for this claim is the material you supplied.\n\nWhere you are not available and replacement is needed, A new supplier will be selected and paid from your balance.\n\nThank you.', 'SUPPLIER_WARRANTY_CLAIM_NOTIFICATION', '2021-06-14 19:42:17', '2021-06-24 22:56:56', NULL),
(27, '5676c492-0029-453a-b536-808ec8d78409', 'Supplier Message Notification', '<p>Dear {firstname} {lastname},</p><p>You have a NEW message from {supplier_name}.</p><p>Please log into your portal to view your message!</p><p><br></p><p>Please click&nbsp;<a href=\"http://127.0.0.1:8000/en/admin/messaging/templates/new/%7Burl%7D\">here</a>&nbsp;to login and view message details.&nbsp;</p><p><br></p><p>Thank you</p><p>FixMaster Team</p>', 'Dear {firstname} {lastname},\n\nYou have a NEW message from {supplier_name}.\n\nPlease log into your portal to view your message!\n\n\n\nPlease click here to login and view message details. \n\n\n\nThank you\n\nFixMaster Team', 'SUPPLIER_SENT_ADMIN_MESSAGE_NOTIFICATION', '2021-06-14 19:44:07', '2021-06-14 19:44:07', NULL),
