(31, '03cdb35e-cc9e-4fbd-a954-7b18d7ef14e0', 'Customer Job Completed Notification', '<p>Hello {first_name} {last_name},</p><p>\n\n\n	\n	\n	\n	\n\n</p><p style=\"margin-bottom: 0.28cm; line-height: 108%\">\nYour&nbsp;job {job_ref} has been completed successfully and a&nbsp;<span style=\"background-color: transparent;\">special {warranty_days}days warranty on job completion is now active with\n</span><b style=\"background-color: transparent;\">FixMaster</b>{warranty_details}<span style=\"background-color: transparent;\">.</span></p><p style=\"margin-bottom: 0.28cm; line-height: 108%\"><span style=\"background-color: transparent;\"><br></span></p><p style=\"margin-bottom: 0.28cm; line-height: 108%\">We look forward to your job rating and review on our website.&nbsp; Thank you for allowing us serve you!</p><style type=\"text/css\">p { margin-bottom: 0.25cm; direction: ltr; line-height: 115%; text-align: left; orphans: 2; widows: 2; background: transparent }a:link { color: #0563c1; text-decoration: underline }</style><style type=\"text/css\">p { margin-bottom: 0.25cm; direction: ltr; line-height: 115%; text-align: left; orphans: 2; widows: 2; background: transparent }a:link { color: #0563c1; text-decoration: underline }</style><style type=\"text/css\">p { margin-bottom: 0.25cm; direction: ltr; line-height: 115%; text-align: left; orphans: 2; widows: 2; background: transparent }a:link { color: #0563c1; text-decoration: underline }</style><style type=\"text/css\">p { margin-bottom: 0.25cm; direction: ltr; line-height: 115%; text-align: left; orphans: 2; widows: 2; background: transparent }a:link { color: #0563c1; text-decoration: underline }</style>', 'Hello {first_name} {last_name}\nYour job {job_ref} has been completed successfully.\nYour special {warranty_days}days warranty on job completion is now active with FixMaster.', 'CUSTOMER_JOB_COMPLETED_NOTIFICATION', '2021-06-14 22:28:24', '2021-07-19 04:15:39', NULL),
(32, '15cebc75-ac39-404c-ba39-cad0f29e5bf4', 'New Job Notification', '<p>\n\n\n	\n	\n	\n	\n\n</p><p style=\"margin-bottom: 0.28cm; line-height: 108%\">\nDear {firstname} {lastname}<br></p>\n<p style=\"margin-bottom: 0.28cm; line-height: 108%\">A new job request\nis available for execution near you. Kindly login to your account to\nACCEPT the job NOW!</p>\n<p style=\"margin-bottom: 0.28cm; line-height: 108%\"><font color=\"#222222\"><font face=\"Arial, serif\"><font style=\"font-size: 10pt\" size=\"2\">Please\nclick </font></font></font><font color=\"#222222\"><font face=\"Arial, serif\"><font style=\"font-size: 10pt\" size=\"2\">{url} to\nview details and accept the job request. </font></font></font>\n</p>\n<p style=\"margin-bottom: 0.28cm; line-height: 108%\"><font color=\"#222222\"><font face=\"Arial, serif\"><font style=\"font-size: 10pt\" size=\"2\">Your\njob rating counts! Remember to request for a rating and review on all\ncompleted diagnosis and job repairs completed. &nbsp;</font></font></font></p>\n\n<p><style type=\"text/css\">p { margin-bottom: 0.25cm; direction: ltr; line-height: 115%; text-align: left; orphans: 2; widows: 2; background: transparent }a:link { color: #0563c1; text-decoration: underline }</style></p>', 'Dear {firstname} {lastname}\n\nA new job request is available for execution near you. Kindly login to your account to ACCEPT the job NOW!\n\nPlease click {url} to view details and accept the job request.\n\nYour job rating counts! Remember to request for a rating and review on all completed diagnosis and job repairs completed.', 'CSE_NEW_JOB_NOTIFICATION', '2021-06-15 16:34:11', '2021-06-15 16:34:11', NULL),
