(1, '5cf39057-f133-404a-8d56-473ede9ebdba', 'Welcome User', '<p>Hello&nbsp;{firstname}&nbsp;{lastname}, thank you for registering with us. Kindly use this link&nbsp;<a href=\"{url}\">click here</a> to verify your account.</p><p><a href=\"http://{url}\" target=\"_blank\">here</a></p>', 'Hello {firstname} {lastname}, thank you for registering with us. Kindly use this link {url} to verify your account.', 'USER_EMAIL_VERIFICATION', '2021-05-28 05:46:57', '2021-05-28 06:46:02', NULL),
(3, '51f55df7-b907-4758-9330-8d2e7a322220', 'CSE Assign QA', '<p>Dear {qa_name},</p><p>A QA Support request for {job_ref} has been assigned to you successfully. Please view the job details below and be on standby; the Customer Service Executive will contact you shortly.</p><p><b>JOB DETAILS</b></p><ul><li><b>CSE ASSIGNED: {cse_name}</b></li><li><b>EMAIL: {cse_email}</b></li><li><b>TELEPHONE: {cse_phone}</b></li><li><b>JOB SERVICE CATEGORY: {service_category}</b></li><li><b>SUB-SERVICE CATEGORY: {sub_service_category}</b></li><li><b>JOB STATUS: {job_status}</b></li><li><b><br></b></li><li>Please click here to view images, videos and additional details of {job_ref}</li></ul><p>Remember, your expertise will aid the prompt completion of the {job_status}!</p><p>Your job rating counts! Remember to requests for a rating from the CSE when the {job_status} is completed.</p><p>Yours Sincerely</p><p>FixMaster Team</p>', 'Dear {qa_name},\n\nA QA Support request for {job_ref} has been assigned to you successfully. Please view the job details below and be on standby; the Customer Service Executive will contact you shortly.\n\nJOB DETAILS\n\nCSE ASSIGNED: {cse_name}\nEMAIL: {cse_email}\nTELEPHONE: {cse_phone}\nJOB SERVICE CATEGORY: {service_category}\nSUB-SERVICE CATEGORY: {sub_service_category}\nJOB STATUS: {job_status}\n\nPlease click here to view images, videos and additional details of {job_ref}\nRemember, your expertise will aid the prompt completion of the {job_status}!\n\nYour job rating counts! Remember to requests for a rating from the CSE when the {job_status} is completed.\n\nYours Sincerely\n\nFixMaster Team', 'CSE_ASSIGNED_QA_TO_A_JOB', '2021-05-28 09:36:19', '2021-05-28 09:36:19', NULL),
(4, '35eea323-77ea-4256-9452-4bdf85e9a815', 'Admin Assign QA', '<p><font face=\"Helvetica\">Dear {qa_name},&nbsp;</font></p><p><font face=\"Helvetica\">A QA support request for {job_ref} has been assigned to you successfully.&nbsp;</font></p><p><font face=\"Helvetica\">Please view the job details below and be on standby; the Customer Service Executive will contact you shortly.</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\"><b>JOB DETAILS</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • CSE ASSIGNED: {cse_name}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • EMAIL: {cse_email}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • TELEPHONE: {cse_phone no}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • JOB SERVICE CATEGORY: {category}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SERVICE CATEGORY: {service}&nbsp;&nbsp;</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SUB-SERVICE CATEGORY: {sub_service}&nbsp;</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • JOB STATUS: {job_status}</b></font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Please click here to view images, videos and additional details of {job_ref}&nbsp;</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Remember, your expertise will aid the prompt completion of the {job_status}!</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Your job rating counts! Remember to request for a rating from the CSE when the {job_status} is completed.&nbsp;&nbsp;</font></p><p><font face=\"Helvetica\">Yours Sincerely</font></p><p><font face=\"Helvetica\">FixMaster Team</font></p>', 'Dear {qa_name},\nA QA support request for {job_ref} has been assigned to you successfully. \n\nPlease view the job details below and be on standby; the Customer Service Executive will contact you shortly.', 'ADMIN_ASSIGNED_QA_TO_A_JOB', '202