(5, '49a1f5b1-f220-4ee7-8070-c33768015c35', 'Admin Job Reminder', '<p><font face=\"Helvetica\">Dear {cse_name},</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Here is a reminder that {job_ref} is pending. Please view the job details below and complete the job on schedule.</font></p><p><font face=\"Helvetica\">JOB DETAILS</font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • TECHNICIAN ASSIGNED: {technician_name}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • TECHNICIAN TELEPHONE: {technician_phone_no}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • QA TECHNICIAN ASSIGNED: {qa_technician_phone_no}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • JOB SERVICE CATEGORY: {category}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SERVICE CATEGORY: {service}&nbsp;&nbsp;</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SUB-SERVICE CATEGORY: {sub_service}&nbsp;</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • JOB STATUS: {job_status}</b></font></p><p><font face=\"Helvetica\"><b>&nbsp; &nbsp; • SCHEDULED DATE/TIME: {date_time}</b></font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Please click here to view images, videos and additional details of {job_ref}.</font></p><p><font face=\"Helvetica\">Thank you</font></p><p><font face=\"Helvetica\">FixMaster Team</font></p>', 'Dear {cse_name},\n\n\n\nHere is a reminder that {job_ref} is pending. Please view the job details below and complete the job on schedule.', 'ADMIN_PENDING_JOB_REMINDER_NOTIFICATION', '2021-05-28 10:05:29', '2021-05-28 10:05:29', NULL),
(6, '87c4e531-22f9-4b97-b424-c65f4da7ba63', 'Admin Message', '<p><font face=\"Helvetica\">Dear {admin_title},</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">You have a NEW message from the FixMaster {admin_title}.&nbsp;</font></p><p><font face=\"Helvetica\">Please log into your portal to view your message!</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Please click here to login to the Admin portal.&nbsp;</font></p><p><font face=\"Helvetica\"><br></font></p><p><font face=\"Helvetica\">Thank you</font></p><p><font face=\"Helvetica\">FixMaster Team</font></p>', 'Dear {admin_title},\n\nYou have a NEW message from the FixMaster {admin_title}.\nPlease log into your portal to view your message.\nPlease click here to login to the Admin portal. \n\nThank you', 'ADMIN_SENT_ADMIN_MESSAGE_NOTIFICATION', '2021-05-28 10:08:54', '2021-05-28 10:08:54', NULL),
